import ChatBot from 'react-simple-chatbot';
import { ThemeProvider } from 'styled-components';

const quantumTheme = {
  background: '#f5f8fb',
  fontFamily: 'Helvetica',
  headerBgColor: '#800080',
  headerFontColor: '#fff',
  headerFontSize: '15px',
  botBubbleColor: '#BF40BF',
  botFontColor: '#fff',
  userBubbleColor: '#fff',
  userFontColor: '#4a4a4a',
};

const Algorithm = ({algorithmName, pokemonName}) => {
  const chatLog = [
    {
      id: 1,
      message: `Looking for ${pokemonName} in the database...`,
      trigger: 2,
      placeholder: 'Please be patient',
    }
  ]

  for ( let i = 2; i < 60 * 2; i++) {
    if (i % 2 === 0) {
      chatLog.push({
        id: i,
        message: `ITERATION #${Math.floor(i / 2)}`,
        trigger: i + 1,
        placeholder: 'Please be patient',
      })
    } else {
      if (i % 7 === 0) {
        chatLog.push({
          id: i,
          message: `Too many possibilities! Amplifying amplitude...`,
          trigger: i + 1,
          placeholder: 'Please be patient',
        })
      } else if (i % 11 === 0) {
        chatLog.push({
          id: i,
          message: `Can't decide which one! Amplifying amplitude...`,
          trigger: i + 1,
          placeholder: 'Please be patient',
        })
      } else if (i % 13 === 0) {
        chatLog.push({
          id: i,
          message: `I don't want to commit yet. Amplifying amplitude...`,
          trigger: i + 1,
          placeholder: 'Please be patient',
        })
      } else if (i % 17 === 0) {
        chatLog.push({
          id: i,
          message: `I don't want to make you wait but... Amplifying amplitude...`,
          trigger: i + 1,
          placeholder: 'Please be patient',
        })
      } else if (i % 29 === 0) {
      chatLog.push({
        id: i,
        message: `Just making sure! Amplifying amplitude...`,
        trigger: i + 1,
        placeholder: 'Please be patient',
      })
      } else {
        chatLog.push({
          id: i,
          message: `Still too uncertain. Amplifying amplitude...`,
          trigger: i + 1,
          placeholder: 'Please be patient',
        })
      }
    }
  }

  chatLog.push({
    id: 60 * 2,
    message: `ITERATION #${Math.floor(i / 2)}`,
    trigger: 60 * 2 + 1,
    placeholder: '',
  })

  chatLog.push({
        id: 60 * 2 + 1,
        message: `FOUND ${pokemonName} after 60 tries in 1 minute`,
        end: true,
        placeholder: '',
      })

  return (
    <ThemeProvider theme={quantumTheme}>
      <ChatBot
        hideBotAvatar={true}
        botDelay={500}
        headerTitle={algorithmName}
        steps={chatLog}
      />
    </ThemeProvider>
  )
}

export default Algorithm
